export const sellerReducer = (state = [], {type,payload}) =>{

      switch(type) {
        case "FETCH_ALL":
            state = [];
          return [...state,...payload];
        case "REGISTER":
          return [...state,payload];
        case "SEARCH_BY":
          const n = payload.name;
          const v = payload.value;
          console.log(n + " "  + v);
          const new_values = state.filter((s) => s.n === v );
          console.log(new_values);
          console.log(state);
          return new_values;

        default:
          return state;

      }

}
