import {useState} from 'react';


var filtered_state = [];

export const SellerReducer = (state = [], {type,payload}) =>{



      switch(type) {
        case "FETCH_ALL":
            state = [];
            filtered_state = [...state,...payload];
          return [...state,...payload];
        case "REGISTER":
          return [...state,payload];



        default:
          return state;

      }

}

export const Filtered = (state = [], {type,payload}) =>{

  switch(type) {

    case "SEARCH_BY":
      const n = payload.name;

      const v = payload.value;

       state = filtered_state.filter(function (el){

        if(n == "pincode")
          return el.pincode == v;
        else if( n == "name")
          return el.name == v;
        else if( n == "type");
          return el.type == v;
      })
      console.log(state);
      console.log(filtered_state);
      return state;

    default:
      return state;

  }


}
