import React,{useState} from 'react'
import {useDispatch} from 'react-redux';
import {useHistory} from 'react-router-dom';
import {customerSignin,customerSignup,sellerSignup,SellerSignin} from '../../actions/auth.js';
import { Typography,TextField, Button } from '@material-ui/core';
import './Home.css';
import { sellerSignin } from '../../api/index.js';
const Home = () => {
    const [name,setName] = useState('');
    const [email,setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [person,setPerson] = useState('');
    const [login,setLogin] = useState(false);
    const [signin,setSignin] = useState(true);

    const dispatch = useDispatch();
    const history = useHistory();

    const l = () => {
        setLogin(true);
        setSignin(false);
    }
    const s = () => {
        setLogin(false)
        setSignin(true);
    }

    const handleSubmit = (e) => {

        e.preventDefault();

        const data = {
            name: name,
            email: email,
            password: password,
        }

        if(person === 'Customer')
        {
            if(login)
            {
                dispatch(customerSignin(data,history));
            }
            else
            {
                dispatch(customerSignup({data,history}));
            }
        }
        else if(person === 'Seller')
        {
            if(login)
            {
                dispatch(sellerSignin({data,history}));
            }
            else
            {
                dispatch(sellerSignup({data,history}));
            }
        }

    }

    return (
        <div className='container'>
            <Typography variant='h3' color="primary" className='quote'>#getVaccinated</Typography>
            <Typography variant='h4' color="primary" className='instruction'>Please register accordingly if you are a customer or a seller</Typography>
            <br/>
            <div >
                    <form className='form' onSubmit={(e) => handleSubmit(e)}>
                        <div>
                            <Button  className='verify'variant="contained" color="primary" size="small" onClick={() => l()}>Log in</Button>
                            <Button  className='verify' variant="contained" color="primary" size="small" onClick={() => s()}>Sign in</Button>
                        </div>
                        <br/>
                    {login ? null : <TextField name="name" className="input" variant="filled" label="Name"  color="primary" onChange={(e) => setName(e.target.value)}/>}
                    <TextField name="email" className="input" variant="filled" label="Email"  onChange={(e) => setEmail(e.target.value)}/>
                    <TextField name="password" className="input" variant="filled" label="Password"  onChange={(e) => setPassword(e.target.value)}/>
                    <TextField name="person" className="input" variant="filled" label="Customer/Seller"  onChange={(e) => setPerson(e.target.value)}/>
                    <Button type='submit' className="input" variant="contained" color="primary" size="small">Submit</Button>
                    </form>
            </div>
        </div>
    )
}

export default Home
