export const sellerReducer = (state = [],filtered_state = [], {type,payload}) =>{

      switch(type) {
        case "FETCH_ALL":
            state = [];
          return [...state,...payload];
        case "REGISTER":
          return [...state,payload];
        case "SEARCH_BY":
          const n = payload.name;
          console.log(typeof(n));
          console.log(typeof(state.name))
          const v = payload.value;
          console.log(n + " "  + v);
           filtered_state = state.filter(function (el){
            console.log(el.pincode);
            if(n == "pincode")
              return el.pincode == v;
            else if( n == "name")
              return el.name == v;
            else if( n == "type");
              return el.type == v;
          })

          console.log(state);
          return filtered_state;


        default:
          return state;

      }

}
