import React,{useState,useEffect} from 'react'
import { useSelector,useDispatch } from 'react-redux';
import {fetchData,searchBy} from '../../actions/movie.js';
import {TextField,Button} from '@material-ui/core';


import './Customer.css';
const Customer = () => {

    const dispatch = useDispatch();
    const [loading,setLoading] = useState(true);
    const [filter,setFilter] = useState(false);
    const [name,setName] = useState('');
    const [value,setValue] = useState('');
    var Movie_Details = useSelector((state) => filter ? state.Filtered : state.allSeller);
    const navbar_state = useSelector((state) => state.navbar)
    let m = navbar_state ? 'main_container_open' : 'normal';
    //console.log(Movie_Details);
    useEffect(() => {
        dispatch(fetchData);
    },[]);

    const l = Movie_Details.length;

    const handleSubmit = (e) => {
        e.preventDefault();

        dispatch(searchBy(name,value));
        setFilter(true);
    }

    return (

        <div className={m}>
               {l ? <div className='main_container'>
                    <h2>Seller Details</h2>
                    <h3>Search By: </h3>

                    <form className='form' onSubmit={(e) => handleSubmit(e)}>
                            <TextField name="name" className="input" variant="outlined" label="Name,Email,Type,Pincode"  color="primary" onChange={(e) => setName(e.target.value)}/>
                            <TextField name="name" className="input" variant="outlined" label="Value"  color="primary" onChange={(e) => setValue(e.target.value)}/>
                            <Button type='submit' className="input" variant="contained" color="primary">Submit</Button>
                    </form>
                    {/* <div className='details'>
                    {Movie_Details.map((movie) => {
                        return (<div key={movie.id} className='each'>
                        <p>Title : {movie.title}</p>
                        <p>Rating : {movie.rating}</p>
                        <p>Tag : {movie.tag}</p>
                        <p>Type : {movie.genres}</p>
                        <p>Imdb Id : {movie.ImdbId}</p>
                        </div>)
                    })}
                    </div> */}
                 </div>
                : <h1>Loading</h1> 
                }  


        </div>
    )
}

export default Customer
