import React,{useState,useEffect} from 'react'
import { useSelector,useDispatch } from 'react-redux';
import {fetchData,searchBy} from '../../actions/Seller';
import {TextField,Button} from '@material-ui/core';

import './Customer.css';
const Customer = () => {

    const dispatch = useDispatch();
    const [loading,setLoading] = useState(true);
    const [name,setName] = useState('');
    const [value,setValue] = useState('');
    var seller_Details = useSelector((state) => state.allSeller);
    const navbar_state = useSelector((state) => state.navbar)
    let m = navbar_state ? 'main_container_open' : 'normal';
    console.log(seller_Details);
    useEffect(() => {
        dispatch(fetchData);
    },[]);

    const l = seller_Details.length;
    const d = useSelector((state) => state.allProducts);
    console.log(d);
    const handleSubmit = (e) => {
        e.preventDefault();

        dispatch(searchBy(name,value));

    }

    return (

        <div className={m}>
               {l ? <div className='main_container'>
                    <h2>Seller Details</h2>
                    <h3>Search By: </h3>

                    <form className='form' onSubmit={(e) => handleSubmit(e)}>
                            <TextField name="name" className="input" variant="outlined" label="Name,Email,Type,Pincode"  color="primary" onChange={(e) => setName(e.target.value)}/>
                            <TextField name="name" className="input" variant="outlined" label="Value"  color="primary" onChange={(e) => setValue(e.target.value)}/>
                            <Button type='submit' className="input" variant="contained" color="primary">Submit</Button>
                    </form>
                    <div className='details'>
                    {seller_Details.map((seller) => {
                        return (<div key={seller._id} className='each'>
                        <p>Name : {seller.name}</p>
                        <p>Email : {seller.email}</p>
                        <p>Contact No : {seller.number}</p>
                        <p>Type : {seller.type}</p>
                        <p>Pincode : {seller.pincode}</p>
                        </div>)
                    })}
                    </div>
                 </div>
                : <h1>Loading</h1> 
                }  


        </div>
    )
}

export default Customer
