import {useState} from 'react';


var filtered_state = [];

export const MovieReducer = (state = [], {type,payload}) =>{



      switch(type) {
        case "FETCH_ALL":
            state = [];
            filtered_state = [...state,...payload];
          return [...state,...payload];
        case "ADD":
          const sn = state[payload];
          sn.mybool = 1;
          return state.map((s,index) => (index === payload ? sn : s))
        case "FAV":
            state = [];
          return [...state,...payload];

        default:
          return state;

      }

}

export const Filtered = (state = [], {type,payload}) =>{

  switch(type) {

    case "SEARCH_BY":
      const n = payload.name;

      const v = payload.value;

       state = filtered_state.filter(function (el){

          if(n=="Rating")
          return el.rating == v;
          else if(n== "Title")
          return el.title == v;
          else if(n== "Genre")
          return el.genres == v;
          else if(n == "Tag")
          return el.tag == v;
          else if(n == "ImdbId")
          return el.ImdbId == v;

      })
      // console.log(state);
      // console.log(filtered_state);
      return state;

    case "RESET":
      console.log(filtered_state);
      state = filtered_state;
      return state;  


    default:
      return state;

  }


}
