export const sellerReducer = (state = [], {type,payload}) =>{

      switch(type) {
        case "FETCH_ALL":
            state = [];
          return [...state,...payload];
        case "REGISTER":
          return [...state,payload];
        case "SEARCH_BY":
          const n = payload.name;
          console.log(typeof(n));
          console.log(typeof(state.name))
          const v = payload.value;
          console.log(n + " "  + v);
          const new_values = state.filter(function (el){
            console.log(el);
            return el.n == v;
          })
          console.log(new_values);
          console.log(state);
          return state;


        default:
          return state;

      }

}
