var filtered_state = [];

export const sellerReducer = (state = [], {type,payload}) =>{

      switch(type) {
        case "FETCH_ALL":
            state = [];
          return [...state,...payload];
        case "REGISTER":
          return [...state,payload];
        case "SEARCH_BY":
          const n = payload.name;

          const v = payload.value;

           filtered_state = state.filter(function (el){

            if(n == "pincode")
              return el.pincode == v;
            else if( n == "name")
              return el.name == v;
            else if( n == "type");
              return el.type == v;
          })
          console.log(filtered_state);
          console.log(state);
          return filtered_state;


        default:
          return state;

      }

}
