import * as api from '../api/index.js';

export const customerSignin = async (formData,router) => async(dispatch) =>{
    try{
        const {data} = await api.customerSignin(formData);
        dispatch({type: 'AUTH', data});

        router.push('/customer');
    } catch(error) {
        console.log(error);
    }
};

export const customerSignup = async (formData,router) => async(dispatch) =>{
    try{
        const {data} = await api.customerSignup(formData);
        dispatch({type: 'AUTH', data});

        router.push('/customer');
    } catch(error) {
        console.log(error);
    }
};

export const SellerSignin = async (formData,router) => {
    try {
        const {data} = await api.sellerSignin(formData);
        dispatch({type: AUTH, data});
        router.push('/seller');
    } catch (error) {
        console.log(error);
    }
}

export const sellerSignup = async (formData,router) => {
    try {
        const {data} = await api.sellerSignup(formData);
        dispatch({type: AUTH, data});
        router.push('/seller');
    } catch (error) {
        console.log(error);
    }
}
